Copy code
<template>
  <!-- ... your template code ... -->
  <b-modal v-model="modalVisible" title="Create Shoe">
    <b-form @submit.prevent="createShoe">
      <b-form-group label="Brand" label-for="brand">
        <b-form-input id="brand" v-model="brand" required></b-form-input>
      </b-form-group>
      
      <!-- Add other form fields (model, size, color, price) -->

      <b-form-group label="Image" label-for="image">
        <b-form-file v-model="image" accept="image/*"></b-form-file>
      </b-form-group>

      <b-button type="submit">Submit</b-button>
    </b-form>
  </b-modal>
  <!-- ... your template code ... -->
</template>

<script>
import axios from 'axios';

export default {
  // eslint-disable-next-line vue/multi-word-component-names
  name: 'Shoe',
  data() {
    return {
      modalVisible: false,
      brand: '',
      image: null, // Variable to store the selected image file
    };
  },
  methods: {
    openModal() {
      this.modalVisible = true;
    },
    closeModal() {
      this.modalVisible = false;
    },
    createShoe() {
      // Create a FormData object to send the form data
      const formData = new FormData();
      formData.append('brand', this.brand);
      // Append other form fields (model, size, color, price) to the FormData object
      // Example:
      formData.append('model', 'YourModelValue');
      formData.append('size', 'YourSizeValue');
      formData.append('color', 'YourColorValue');
      formData.append('price', 'YourPriceValue');
      formData.append('image', this.image);

      // Make a POST request to the backend endpoint
      axios.post('http://localhost:8080/create', formData)
        .then(response => {
          console.log(response.data);
          // Handle the response as needed
        })
        .catch(error => {
          console.error('Error:', error);
          // Handle errors
        })
        .finally(() => {
          // Close the modal regardless of success or failure
          this.closeModal();
        });
    },
  },
};
</script>


<style>
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
}

.modal-container {
  background: #fff;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
}

.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.close-btn {
  cursor: pointer;
}

.modal-body {
  margin-top: 20px;
}

/* Modal fade animation */
.modal-fade-enter-active,
.modal-fade-leave-active {
  transition: opacity 0.5s;
}
.modal-fade-enter, .modal-fade-leave-to /* .modal-fade-leave-active in <2.1.8 */ {
  opacity: 0;
}
</style>
